import React, { useContext } from 'react';
import styles from '../styles/topbar.module.css';
import { ThemeContext } from '../context/ThemeContext'; // Importando o ThemeContext

function TopBar() {
  const { theme } = useContext(ThemeContext); // Obtendo o tema do contexto

  return (
    <header className={styles.topBar}>
      <div className={styles.topBarItem}>
        <div className={styles.topBarContent}>
          <h3>Faturamento</h3>
          <p id="faturamento-value">R$ 50,000</p>
        </div>
        <div className={styles.topBarIcon}>
          <span className="material-icons" style={{ color: theme === 'light' ? 'black' : 'yellow' }}>attach_money</span>
        </div>
      </div>
      {/* Repita o mesmo padr√£o para os outros itens */}
    </header>
  );
}

export default TopBar;
