import React from 'react';

function TopBar({ salesData }) {
  const latestData = salesData[salesData.length - 1];

  return (
    <header className="top-bar">
      {['Faturamento', 'Vendas', 'Lucro', 'Publicidade'].map((item, index) => (
        <div className="top-bar-item" key={index}>
          <div className="top-bar-content">
            <h3>{item}</h3>
            <p id={`${item.toLowerCase()}-value`}>
              {item === 'Vendas' ? latestData.vendas : `R$ ${latestData[item.toLowerCase()].toLocaleString('pt-BR', { minimumFractionDigits: 2 })}`}
            </p>
          </div>
          <div className="top-bar-icon">
            <span className="material-icons">{item === 'Faturamento' ? 'attach_money' : item === 'Vendas' ? 'shopping_cart' : item === 'Lucro' ? 'trending_up' : 'campaign'}</span>
          </div>
        </div>
      ))}
    </header>
  );
}

export default TopBar;
